mapper.update:
update myboard 
set board_title = #{boardTitle}, 
    board_content = #{boardContent},
    board_writer = #{boardWriter}
where board_no = #{boardNo}

mapper.select:
select count(*) as cnt
  from myboard
  where board_no = #{boardNo}


