)
values
(
#{atchFileId}
,(select nvl(max(file_sn), 0) + 1
  from atch_file_detail
  where atch_file_id = #{atchFileId})
,#{fileStreCours}
,#{streFileNm}
,#{orignlFileNm}
,#{fileExtsn}
,#{fileCn}
,#{fileSize}
)

mapper.select:
select * from atch_file_detail
where atch_file_id = #{atchFileId}
